cancer_data1 |&gt;
  select(Estrogen_Receptor_Status, Progesterone_Receptor_Status, 
         Her2_Receptor_Status) |&gt;
  pivot_longer(cols = everything(), 
               names_to = "Receptor", values_to = "Status") |&gt;
  drop_na(Status) |&gt;
  group_by(Status, Receptor) |&gt;
  summarize(count = n()) |&gt;
